
from Crypto.PublicKey import RSA
from factordb.factordb import FactorDB
from Crypto.Util.number import inverse,long_to_bytes
import base64

#we get ct from steghide when we input given picture

f = open(r'/home/matea/Downloads/pub.pem','r')
key = RSA.importKey(f.read())

print(key.n,key.e)


f=FactorDB(key.n)
f.connect()
p=f.get_factor_list()[0]
q=f.get_factor_list()[1]

phi=(q-1)*(p-1)

d=inverse(key.e,phi)

m=pow(ct,d,key.n)

print(base64.b64decode(long_to_bytes(m)))


